
-- Table: config
CREATE TABLE config (name VARCHAR (100) PRIMARY KEY, value VARCHAR (200), value2 BLOB)


-- Table: products
CREATE TABLE products (id INTEGER PRIMARY KEY AUTOINCREMENT, en_US VARCHAR (50), fr_FR VARCHAR (50), es_ES VARCHAR (50), th_TH VARCHAR (50), grouped_products VARCHAR (50), steps INTEGER, field_id_fabric_id INTEGER, matching_fields TEXT, show INTEGER, user_active INTEGER, fieldorder INT DEFAULT (0), active INTEGER)


-- Table: products_pages
CREATE TABLE products_pages (id INTEGER PRIMARY KEY AUTOINCREMENT, product_id INTEGER, page_id INTEGER, en_US VARCHAR (100), fr_FR VARCHAR (100), es_ES VARCHAR (100), th_TH VARCHAR (100))


-- Table: products_options
CREATE TABLE products_options (id INTEGER PRIMARY KEY AUTOINCREMENT, product_id INTEGER, parent_id INTEGER, screen INTEGER, display_label INTEGER, name VARCHAR (100), en_US VARCHAR (100), fr_FR VARCHAR (100), es_ES VARCHAR (100), th_TH VARCHAR (100), option_group VARCHAR (100), sort INTEGER, type VARCHAR (50), hidden INTEGER, css VARCHAR (100), class VARCHAR (100), required INTEGER DEFAULT (1), active INTEGER)


-- Table: products_options_values
CREATE TABLE products_options_values (id INTEGER PRIMARY KEY AUTOINCREMENT, option_id INTEGER REFERENCES products_options (id) ON UPDATE CASCADE, en_US VARCHAR (100), fr_FR VARCHAR (100), es_ES VARCHAR (100), th_TH VARCHAR (100), image VARCHAR (100), selected BOOLEAN DEFAULT false, onselected VARCHAR (255), fieldorder INT DEFAULT (0), active INTEGER)


-- Table: orders
CREATE TABLE orders (id INTEGER PRIMARY KEY AUTOINCREMENT, reseller_order_id VARCHAR (100), supplier_order_id VARCHAR (100), client_id INTEGER REFERENCES clients (id), measurements_id INTEGER, ordered_date DATE, expected_delivery_date DATE, shipped_date DATE, delivered_date DATE, payment_status VARCHAR (100), payment_date DATE, payment_details TEXT, order_status VARCHAR (100))


-- Table: orders_lines
CREATE TABLE orders_lines (id INTEGER PRIMARY KEY AUTOINCREMENT, order_id INTEGER REFERENCES clients (id), parent_id INTEGER, product_id INTEGER, product_options TEXT, supplier_line_id INTEGER, name TEXT, fabric_id TEXT, quantity INTEGER, status VARCHAR (100), tracking_number VARCHAR (100), comments TEXT)


-- Table: clients
CREATE TABLE clients (id INTEGER PRIMARY KEY AUTOINCREMENT, company VARCHAR (100), firstname VARCHAR (100), lastname VARCHAR (100), gender VARCHAR (100), email VARCHAR (100), phone VARCHAR (100), photo BLOB, photo_front BLOB, photo_side BLOB, photo_back BLOB, dob DATE, street1 VARCHAR (100), street2 VARCHAR (100), city VARCHAR (100), postalcode VARCHAR (100), state VARCHAR (100), country VARCHAR (100), language VARCHAR (5), applanguage VARCHAR (5), currency VARCHAR (5), notes TEXT, orders_lastdate DATE, orders_nbr INTEGER, active BOOLEAN)


-- Table: measurements
CREATE TABLE measurements (id INTEGER PRIMARY KEY AUTOINCREMENT, client_id INTEGER, date VARCHAR (100), type VARCHAR (100), unit_size VARCHAR (100), unit_weight VARCHAR (100), jacket_neck VARCHAR (10), jacket_fullshoulder VARCHAR (10), jacket_chest VARCHAR (10), jacket_front VARCHAR (10), jacket_back VARCHAR (10), jacket_stomach VARCHAR (10), jacket_hips VARCHAR (10), jacket_lengthfront VARCHAR (10), jacket_lengthback VARCHAR (10), jacket_sleeve VARCHAR (10), jacket_armsize VARCHAR (10), jacket_cuff VARCHAR (10), pants_waist VARCHAR (10), pants_hips VARCHAR (10), pants_crotch VARCHAR (10), pants_thigh VARCHAR (10), pants_length VARCHAR (10), pants_bottom VARCHAR (10), shoes_size VARCHAR (10), waistcoat_lengthfront VARCHAR (10), waistcoat_lengthback VARCHAR (10), silhouette_height VARCHAR (10), silhouette_weight VARCHAR (10), silhouette_shoulder VARCHAR (15), silhouette_neck VARCHAR (15), silhouette_posture VARCHAR (15), silhouette_chest VARCHAR (15), comments TEXT, active BOOLEAN)






